CREATE DEFINER=`root`@`localhost` PROCEDURE `GetResult`(
IN eid int,
IN uid int,
IN egid int
)
BEGIN
DECLARE attemptedExamCount int default 0;
CREATE TEMPORARY TABLE IF NOT EXISTS `temp_result`
WITH CTE as(
 select AE.UserId,EG.ExamId,EG.ExamGroupId,E.Title as ExamTitle,U.FirstName,U.LastName,U.Email,E.TotalMarks,E.PassMarks,
(select count(UserAnswerId) from useranswer  where  GivenAnswerId=''and ExamGroupId=EG.ExamGroupId and UserId=AE.UserId) as NotAttemptedQuestions,
(select count(UserAnswerId) from useranswer  where  GivenAnswerId<>''and ExamGroupId=EG.ExamGroupId and UserId=AE.UserId) as AttemptedQuestions,
(select GetObtainMarks(egid,uid)) as ObtainMarks,
(select CHAR_LENGTH(uq.QuestionIds) - CHAR_LENGTH(REPLACE(uq.QuestionIds, ',', ''))) + 1 as TotalQuestions
from exam_group EG 
LEFT JOIN AssignExam AE ON AE.ExamGroupId=EG.ExamGroupId
LEFT JOIN Exams E ON EG.ExamId=E.ExamId
LEFT JOIN User U ON AE.UserId=U.UserId 
LEFT JOIN userquestion uq on EG.ExamGroupId=uq.ExamGroupId  and uq.UserId=AE.UserId
WHERE EG.ExamGroupId=egid and AE.UserId=uid
)
SELECT *,IFNULL(ROUND(cast((ObtainMarks/TotalMarks)*100 as float),0),0) as Percentage,IF(ObtainMarks < PassMarks ,0,1) as Result FROM CTE;
SET attemptedExamCount=(select count(*) from attemptedexams atpex where atpex.ExamGroupId=egid and UserId=uid);
IF(attemptedExamCount=0)THEN
Insert into attemptedexams(ExamGroupId,ExamId,UserId,TotalQuestions,NotAttemptedQuestions,AttemptedQuestions,MarksObtained,Percentage,Result,ExamDate,ExamStatusId)
(select ExamGroupId,ExamId,UserId,TotalQuestions,NotAttemptedQuestions,AttemptedQuestions,ObtainMarks,Percentage,Result,NOW(),3 from temp_result);
ELSE 
UPDATE attemptedexams a
         INNER JOIN temp_result t ON a.ExamGroupId = t.ExamGroupId and a.UserId=t.UserId
SET a.TotalQuestions = t.TotalQuestions,a.NotAttemptedQuestions=t.NotAttemptedQuestions,a.AttemptedQuestions=t.AttemptedQuestions,a.MarksObtained=t.ObtainMarks,a.Percentage=t.Percentage,a.Result=t.Result,a.ExamDate=Now(),a.ExamStatusId=3 where a.ExamGroupId=egid and a.UserId=uid;
END IF;
select * from temp_result;
drop table temp_result;
end ;;
