CREATE DEFINER=`root`@`localhost` PROCEDURE `str_split`(
IN str VARCHAR(4000)
)
BEGIN
DECLARE delimIdx int default 0;
DECLARE charIdx int default 1;
DECLARE rest_str varchar(4000) default '';
DECLARE store_str varchar(4000) default '';
create TEMPORARY table IF NOT EXISTS tempids ( val int);
truncate table tempids;
set @rest_str = str;
set  @delimIdx = LOCATE(',',@rest_str);
set @charIdx = 1;
set @store_str = SUBSTRING(@rest_str,@charIdx,@delimIdx-1);
set @rest_str = SUBSTRING(@rest_str from @delimIdx+1);
if length(trim(@store_str)) = 0   then
    set @store_str = @rest_str;
end if;
INSERT INTO tempids
SELECT (@store_str + 0);
WHILE @delimIdx <> 0 DO
    set  @delimIdx = LOCATE(',',@rest_str);
    set @charIdx = 1;
    set @store_str = SUBSTRING(@rest_str,@charIdx,@delimIdx-1);
    set @rest_str = SUBSTRING(@rest_str from @delimIdx+1);

    if length(trim(@store_str)) = 0   then
        set @store_str = @rest_str;
    end if;
    INSERT INTO tempids(val)
    SELECT (@store_str + 0);
END WHILE;
#select val from tempids;
END ;;
